red.Blue = 0;
PdfPen pen = new PdfPen(red);
PdfBrush brush = new PdfSolidBrush(red);
// Draw the rectangle
page.Graphics.DrawRectangle(pen, rect);
doc.Save("CalRedColorSpace.pdf");


